import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import styled, { ThemeProvider } from 'styled-components';
import { Toaster } from 'react-hot-toast';
import { motion } from 'framer-motion';

// Components
import Header from './components/Header';
import WanjikuAvatar from './components/WanjikuAvatar';
import ChatInterface from './components/ChatInterface';
import VoiceInterface from './components/VoiceInterface';
import ServicesList from './components/ServicesList';
import DocumentManager from './components/DocumentManager';
import Navigation from './components/Navigation';

// Theme
const theme = {
  colors: {
    primary: '#2E8B57',
    secondary: '#FF6B35',
    accent: '#4ECDC4',
    background: '#F8F9FA',
    text: '#2C3E50',
    white: '#FFFFFF',
    gray: '#6C757D',
    lightGray: '#E9ECEF',
    success: '#28A745',
    warning: '#FFC107',
    danger: '#DC3545',
    info: '#17A2B8'
  },
  fonts: {
    primary: "'Inter', sans-serif",
    swahili: "'Inter', sans-serif"
  },
  breakpoints: {
    mobile: '768px',
    tablet: '1024px',
    desktop: '1200px'
  }
};

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  width: 100%;
`;

const WelcomeSection = styled(motion.div)`
  text-align: center;
  margin-bottom: 2rem;
  color: white;
`;

const WelcomeTitle = styled.h1`
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
  
  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 2rem;
  }
`;

const WelcomeSubtitle = styled.p`
  font-size: 1.2rem;
  opacity: 0.9;
  margin-bottom: 2rem;
`;

const FeatureGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 2rem;
`;

const FeatureCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 15px;
  padding: 2rem;
  box-shadow: 0 10px 30px rgba(0,0,0,0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const FeatureTitle = styled.h3`
  color: ${props => props.theme.colors.primary};
  font-size: 1.5rem;
  margin-bottom: 1rem;
  font-weight: 600;
`;

const FeatureDescription = styled.p`
  color: ${props => props.theme.colors.text};
  line-height: 1.6;
  margin-bottom: 1rem;
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
`;

const FeatureItem = styled.li`
  color: ${props => props.theme.colors.gray};
  margin-bottom: 0.5rem;
  padding-left: 1.5rem;
  position: relative;
  
  &::before {
    content: 'âœ“';
    position: absolute;
    left: 0;
    color: ${props => props.theme.colors.success};
    font-weight: bold;
  }
`;

function App() {
  const [currentView, setCurrentView] = useState('home');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  if (isLoading) {
    return (
      <ThemeProvider theme={theme}>
        <AppContainer>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            height: '100vh',
            color: 'white',
            fontSize: '1.5rem'
          }}>
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
            >
              âš¡
            </motion.div>
            <span style={{ marginLeft: '1rem' }}>Wanjiku 2.0 inapakia...</span>
          </div>
        </AppContainer>
      </ThemeProvider>
    );
  }

  const renderContent = () => {
    switch (currentView) {
      case 'chat':
        return <ChatInterface />;
      case 'voice':
        return <VoiceInterface />;
      case 'services':
        return <ServicesList />;
      case 'documents':
        return <DocumentManager />;
      default:
        return (
          <>
            <WelcomeSection
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <WelcomeTitle>Karibu Wanjiku 2.0</WelcomeTitle>
              <WelcomeSubtitle>
                Msaidizi wako wa Serikali wa Kenya - Huduma za Kidijitali kwa Wote
              </WelcomeSubtitle>
            </WelcomeSection>

            <FeatureGrid>
              <FeatureCard
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.2 }}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FeatureTitle>ðŸ’¬ Mazungumzo ya Swahili</FeatureTitle>
                <FeatureDescription>
                  Wanjiku anaweza kuzungumza na wewe kwa Kiswahili na kukusaidia na maswali yako.
                </FeatureDescription>
                <FeatureList>
                  <FeatureItem>Mazungumzo ya asili ya Kiswahili</FeatureItem>
                  <FeatureItem>Kujibu maswali ya huduma za serikali</FeatureItem>
                  <FeatureItem>Kusaidia na mchakato wa maombi</FeatureItem>
                </FeatureList>
              </FeatureCard>

              <FeatureCard
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.4 }}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FeatureTitle>ðŸŽ¤ Sauti na Maandishi</FeatureTitle>
                <FeatureDescription>
                  Tumia sauti yako au andika ujumbe kwa Wanjiku.
                </FeatureDescription>
                <FeatureList>
                  <FeatureItem>Kutambua sauti ya Kiswahili</FeatureItem>
                  <FeatureItem>Kujibu kwa sauti</FeatureItem>
